measures.expression:
1 - (
    CALCULATE(
        SUM('Custom SQL Query (dlh_nsec.objdb) (2)'[Measure]),
        FILTER(
            'Custom SQL Query (dlh_nsec.objdb) (2)',
            COUNTROWS(FILTER('Custom SQL Query (dlh_nsec.objdb) (2)', 'Custom SQL Query (dlh_nsec.objdb) (2)'[Measure] <> BLANK())) = -4
        )
    ) / SUM('Custom SQL Query (dlh_nsec.objdb) (2)'[Measure])
)
